import { createReducer, on } from '@ngrx/store';
import { setCount, increment, decrement, reset } from './counter.actions';
export const initialState = 0;
const _counterReducer = createReducer(initialState, on(setCount, (state, {
  count
}) => count), on(increment, state => state + 1), on(decrement, state => state > 0 ? state - 1 : 0), on(reset, () => 0));
export function counterReducer(state, action) {
  return _counterReducer(state, action);
}
